import { Button, Tab ,Box , Input , CircularProgress } from "@material-ui/core"
import Lottery from "../chian-info/contracts/Lottery.json"
import networkMapping from "../chian-info/deployments/map.json";
import { constants , utils } from "ethers";
import { useContractFunction, useEthers } from "@usedapp/core"
import { Contract } from "@ethersproject/contracts"
import React , { useEffect, useState } from 'react'
import { useLottery } from "../hooks/useLottery"
import { makeStyles } from "@material-ui/core"

const useStyles = makeStyles((theme) => ({
    title: {
        color: theme.palette.common.white,
        textAlign: "center",
        padding: theme.spacing(4)
    }
}))


export const Main = () => {

    const { abi } = Lottery
    const {chainId} = useEthers()
    const LotteryAddress = chainId ? networkMapping["42"]["Lottery"][0] : constants.AddressZero
    const { account } = useEthers()
    const LotteryInterface = new utils.Interface(abi)
    const LotteryContract = new Contract(LotteryAddress, LotteryInterface)

    //const  [amount , setAmount ] = useState<number | string | Array<number | string >>(0)
    //const handleInputChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    //    const newAmount = event.target.value === "" ? "" : Number(event.target.value)
    //    setAmount(newAmount)
   // }

    
    
   const { send } = useContractFunction(LotteryContract, "enter" , {})

    const amount = 0.0250
   const amountAsWei = utils.parseEther(amount.toString())
    
    const handleAmountSubmit = () => {
        send({ value: amountAsWei })
    }



    return (
        <Box>
            
            <Button
                onClick={handleAmountSubmit}
                color = "primary"
                variant="contained" 
                size="large"
                //disabled={isMining}
            >
               Enter Lottery 
            </Button>
        </Box> 
    )
}
