import { Button, Tab ,Box , Input , CircularProgress } from "@material-ui/core"
import Lottery from "../chian-info/contracts/Lottery.json"
import networkMapping from "../chian-info/deployments/map.json";
import { constants , utils } from "ethers";
import { useContractFunction, useEthers , useContractCall } from "@usedapp/core"
import { Contract } from "@ethersproject/contracts"
import React , { useEffect, useState } from 'react'
import { useLottery } from "../hooks/useLottery"
import { makeStyles } from "@material-ui/core"
import { TabContext, TabList, TabPanel } from "@material-ui/lab"



const useStyles = makeStyles((theme) => ({
    tabContent: {
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        gap: theme.spacing(4)
    },
    box: {
        margin: "50px",
        marginTop: "25%",
        backgroundColor: "#EDE9E8",
        borderRadius: "25px",
        height: "200px",
        position: "relative"
    },
    header: {
        color: "black"
    },
    button: {
        margin: "0",
        position: "absolute",
        top: "50%",
        left: "50%",
        transform: "translate(-50%)",
        backgroundColor: "#21b6ae",
        '&:hover': {
            background: "#f00",
         },     
    },
    center:{
        margin: "0",
        position: "absolute",
        left: "50%",
        transform: "translate(-50%)",
        fontFamily: 'sans-serif'
    }
}))



export const Main = () => {

    const { abi } = Lottery
    const {chainId} = useEthers()
    const LotteryAddress = chainId ? networkMapping["42"]["Lottery"][0] : constants.AddressZero
    const { account } = useEthers()
    const LotteryInterface = new utils.Interface(abi)
    const LotteryContract = new Contract(LotteryAddress, LotteryInterface)

    //const  [amount , setAmount ] = useState<number | string | Array<number | string >>(0)
    //const handleInputChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    //    const newAmount = event.target.value === "" ? "" : Number(event.target.value)
    //    setAmount(newAmount)
   // }





    
    
   const { send } = useContractFunction(LotteryContract, "enter" , {})

    const amount = 0.0250
   const amountAsWei = utils.parseEther(amount.toString())
    
   const count = useLottery()

   console.log(count)

    const handleAmountSubmit = () => {
        send({ value: amountAsWei })
    }
    const isConnected = account !== undefined

    const classes = useStyles()
    return (
        <Box >
            <Box className={classes.box}>
                <h1 className={classes.center} >Join our Lottery</h1>
            {isConnected ? (
            <Button
                onClick={handleAmountSubmit}
                color = "primary"
                variant="contained" 
                size="large"
                className={classes.button}
                //disabled={isMining}
            >
               Pay 50$ worth ether to enter
            </Button>
             ) : (
                <Button
                onClick={handleAmountSubmit}
                color = "primary"
                variant="contained" 
                size="large"
                className={classes.button}
                disabled={true}
            >
               Connect Your Wallet First
            </Button>
                )} 
            </Box>
        </Box> 
    )
}
